<?php  if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Admin extends CI_Controller {	public function __construct()	{		parent::__construct();		$this->load->database();		$this->load->library(array('ion_auth','form_validation'));		$this->load->helper(array('url','language'));		$this->form_validation->set_error_delimiters($this->config->item('error_start_delimiter', 'ion_auth'), $this->config->item('error_end_delimiter', 'ion_auth'));		$this->lang->load('auth');				$this->_init();				//$this->load->model('news_model');			} 		private function _init()	{		$this->output->set_template('aries_layout');				//$this->load->css('assets/aries/css/stylesheets.css');		//$this->load->css('assets/aries/css/stylesheets.css');				//$this->load->js('assets/themes/default/js/jquery-1.9.1.min.js');		//$this->load->js('assets/themes/default/hero_files/bootstrap-transition.js');		//$this->load->js('assets/themes/default/hero_files/bootstrap-collapse.js');			}	public function Index($page = 'admin'){				$data['title'] = "AA Planetica" ;		$data['activepage'] = "Home" ;				$data = array(		'username' => $this->input->post('username'),		'password' => $this->input->post('password'),		'remember' => $this->input->post('remember'),		);								//$this->output->set_template('aries_layout');		$this->load->view('aries_admin/users/t', $data);		}		public function Login($page = 'login'){						$data['title'] = "AA Planetica" ;		$data['activepage'] = "Home" ;				//$this->output->set_template('aries_layout');		$this->load->view('aries_admin/users/'.$page, $data);		}	}